import React, { useState } from 'react';
import './MovieDetail.css';

function MovieDetail({ movie, onClose }) {
  const [newReview, setNewReview] = useState('');
  const [newRating, setNewRating] = useState(0);
  const [reviews, setReviews] = useState(movie.reviews || []);

  const handleSubmit = () => {
    if (newReview.trim() === '' || newRating === 0) return;

    const reviewObj = { rating: newRating, text: newReview };
    setReviews([...reviews, reviewObj]);
    setNewReview('');
    setNewRating(0);

    // Optional: send to backend
    // fetch(`/api/movies/${movie.id}/review`, { method:'POST', body: JSON.stringify(reviewObj) })
  };

  return (
    <div className="movie-detail">
      <div className="detail-bg" style={{ backgroundImage: `url(${movie.background})` }}></div>

      <div className="detail-content">
        <div className="detail-left">
          <img src={movie.image} alt={movie.title} />
        </div>

        <div className="detail-center">
          <h1>{movie.title}</h1>
          <p><strong>{movie.year}</strong> | Director: {movie.director}</p>
          <p>Hero: {movie.hero} | Heroine: {movie.heroine}</p>
          <p>{movie.summary}</p>
        </div>

        <div className="detail-right">
          <h2>Reviews</h2>

          {/* Existing reviews */}
          {reviews.map((r, idx) => (
            <div key={idx} className="review-item">
              <span className="review-stars">{'★'.repeat(r.rating)+'☆'.repeat(5-r.rating)}</span>
              <p>{r.text}</p>
            </div>
          ))}

          <div className="new-review">
            <h3>Write a Review</h3>
            <div className="star-select">
              {[1,2,3,4,5].map(star => (
                <span
                  key={star}
                  className={star <= newRating ? "filled" : "empty"}
                  onClick={() => setNewRating(star)}
                >★</span>
              ))}
            </div>
            <textarea
              placeholder="Write your review..."
              value={newReview}
              onChange={(e) => setNewReview(e.target.value)}
            />
            <button onClick={handleSubmit}>Submit</button>
          </div>
        </div>
      </div>

      <button className="close-btn" onClick={onClose}>×</button>
    </div>
  );
}

export default MovieDetail;
