import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './MovieCard.css';

function MovieCard() {
  const navigate = useNavigate();

  const movies = [
    {
      title: 'Squid Game',
      year: '2021',
      director: 'Hwang Dong-hyuk',
      hero: 'Lee Jung-jae',
      heroine: 'HoYeon Jung',
      description: 'Contestants play deadly games for huge money.',
      rating: 5,
      img: '/images/squid_game.jpg',
    },
    {
      title: 'Inception',
      year: '2010',
      director: 'Christopher Nolan',
      hero: 'Leonardo DiCaprio',
      heroine: 'Elliot Page',
      description: 'A thief enters dreams to steal secrets.',
      rating: 4,
      img: '/images/inception.jpg',
    },
    {
      title: 'Oppenheimer',
      year: '2023',
      director: 'Christopher Nolan',
      hero: 'Cillian Murphy',
      heroine: 'Emily Blunt',
      description: 'The story of J. Robert Oppenheimer and the atomic bomb.',
      rating: 5,
      img: '/images/oppenheimer.jpg',
    },
    {
      title: 'Parasite',
      year: '2019',
      director: 'Bong Joon-ho',
      hero: 'Song Kang-ho',
      heroine: 'Cho Yeo-jeong',
      description: 'A poor family infiltrates a wealthy household.',
      rating: 4,
      img: '/images/parasite.jpg',
    },
  ];

  const [reviews, setReviews] = useState({});

  const handleReviewChange = (title, value) => {
    setReviews({ ...reviews, [title]: value });
  };

  return (
    <div className="movie-container">
      <h1 className="heading">RifLux! Movie Reviews</h1>
      <div className="movie-list">
        {movies.map((movie, index) => (
          <div className="movie-card" key={index}>
            <img
              src={movie.img}
              alt={movie.title}
              className="movie-img"
              onClick={() => navigate(`/movie/${movie.title}`)} // üëà click to open details
            />
            <h2 className="movie-title" onClick={() => navigate(`/movie/${movie.title}`)}>
              {movie.title}
            </h2>
            <p>
              <b>{movie.year}</b> | Director: {movie.director}<br />
              Hero: {movie.hero} | Heroine: {movie.heroine}<br />
              {movie.description}
            </p>
            <p>{'‚≠ê'.repeat(movie.rating)}</p>
            <textarea
              placeholder="Write your review..."
              value={reviews[movie.title] || ''}
              onChange={(e) => handleReviewChange(movie.title, e.target.value)}
            />
            <button className="submit-btn">Submit Review</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MovieCard;
