import React, { useState } from 'react';
import './MovieCard.css';

function MovieCard({ movie }) {
  // Existing states
  const [rating, setRating] = useState(movie.rating);
  const [review, setReview] = useState('');
  const [reviewsList, setReviewsList] = useState(movie.reviews || []);

  // <-- New state for full-screen modal
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleClick = (star) => setRating(star);

  const handleSubmit = async () => {
    if (review.trim() === '') return;

    const newReview = { rating, text: review };
    setReviewsList([...reviewsList, newReview]);
    setReview('');

    // Optional: save review to backend
    try {
      await fetch(`http://localhost:5000/api/movies/${movie.id}/review`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newReview)
      });
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <>
      {/* Movie card */}
      <div className="movie-card" onClick={() => setIsModalOpen(true)}> {/* <-- Card click opens modal */}
        <img src={movie.image} alt={movie.title} />
        <h2>{movie.title}</h2>
        <p><strong>{movie.year}</strong> | Director: {movie.director}</p>
        <p>Hero: {movie.hero} | Heroine: {movie.heroine}</p>
        <p>{movie.shortStory}</p>

        {/* Rating stars */}
        <div className="stars">
          {[1,2,3,4,5].map(star => (
            <span
              key={star}
              className={star <= rating ? "filled" : "empty"}
              onClick={(e) => { e.stopPropagation(); handleClick(star); }} // prevent modal open
            >★</span>
          ))}
        </div>

        {/* Review box */}
        <textarea
          placeholder="Write your review..."
          value={review}
          onChange={(e) => { e.stopPropagation(); setReview(e.target.value); }} // prevent modal open
        />

        <button onClick={(e) => { e.stopPropagation(); handleSubmit(); }}>Submit Review</button>

        {/* Reviews list */}
        <div className="reviews">
          <h4>User Reviews</h4>
          {reviewsList.length > 0 ? (
            reviewsList.map((r, idx) => (
              <div key={idx} className="review-item">
                <span className="review-stars">{'★'.repeat(r.rating) + '☆'.repeat(5 - r.rating)}</span>
                <p>{r.text}</p>
              </div>
            ))
          ) : (
            <p className="no-reviews">No reviews yet. Be the first to comment!</p>
          )}
        </div>
      </div>

      {/* <-- Full-screen background modal */}
      {isModalOpen && (
        <div className="modal" onClick={() => setIsModalOpen(false)}>
          <img src={movie.background} alt={`${movie.title} Background`} />
        </div>
      )}
    </>
  );
}

export default MovieCard;
