import React from "react";
import "./MovieDetailModal.css";

const MovieDetailModal = ({ movie, onClose }) => {
  return (
    <div className={`modal-overlay open`}>
      <div
        className="modal-background"
        style={{ backgroundImage: `url(${movie.image})` }}
      ></div>

      <div className="modal-content">
        <button className="close-btn" onClick={onClose}>
          ✕
        </button>

        <div className="modal-info">
          <h1>{movie.title}</h1>
          <p className="meta">
            {movie.year} • {movie.seasons} • {movie.genre}
          </p>
          <p className="description">{movie.shortStory}</p>
          <p>
            <strong>Starring:</strong> {movie.starring}
          </p>
          <p>
            <strong>Creators:</strong> {movie.director}
          </p>
          <p>
            <strong>Episodes:</strong> {movie.episodes} | Duration:{" "}
            {movie.duration}
          </p>
        </div>

        <div className="modal-review">
          <h3>⭐ {movie.rating}/5</h3>
          <p>Albert: “Absolutely thrilling! Can't stop watching.”</p>
          <p>Sofia: “Great story, but some parts are intense!”</p>
          <textarea
            placeholder="Write your review..."
            rows="3"
            className="review-input"
          ></textarea>
          <button className="submit-btn">Submit Review</button>
        </div>
      </div>
    </div>
  );
};

export default MovieDetailModal;
