import React, { useEffect, useState } from 'react';
import MovieCard from './components/MovieCard';
import MovieDetail from './components/MovieDetail'; // NEW import
import './App.css';

function App() {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null); // NEW state for detail view

  useEffect(() => {
    fetch('http://localhost:5000/api/movies')
      .then(res => res.json())
      .then(data => setMovies(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="app">
      <h1 style={{color:'#e50914', textAlign:'center', margin:'20px'}}>RifLux! Movie Reviews</h1>

      <div className="movie-grid">
        {movies.map(movie => (
          <MovieCard 
            key={movie.id} 
            movie={movie} 
            onClick={() => setSelectedMovie(movie)} // NEW: click handler
          />
        ))}
      </div>

      {selectedMovie && (
        <MovieDetail 
          movie={selectedMovie} 
          onClose={() => setSelectedMovie(null)} // allow closing detail view
        />
      )}
    </div>
  );
}

export default App;
