import React, { useState, useEffect } from 'react';
import MoviePlatform from './components/MoviePlatform'; // your MoviePlatform.js
import './App.css';

function App() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    // If you want to fetch from API (optional)
    fetch('http://localhost:5000/api/movies')
      .then(res => res.json())
      .then(data => setMovies(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="app">
      <h1 style={{ color: '#e50914', textAlign: 'center', margin: '20px' }}>
        RifLux! Movie Reviews
      </h1>

      {/* MoviePlatform handles movie grid and click-to-open */}
      <MoviePlatform movies={movies} />
    </div>
  );
}

export default App;
