import React, { useState } from 'react';
import './MovieCard.css';

function MovieCard({ movie, onClick }) { // added onClick prop
  const [rating, setRating] = useState(movie.rating);
  const [review, setReview] = useState('');
  const [reviewsList, setReviewsList] = useState(movie.reviews || []);

  const handleClick = (star) => setRating(star);

  const handleSubmit = async () => {
    if (review.trim() === '') return;

    const newReview = { rating, text: review };
    setReviewsList([...reviewsList, newReview]);
    setReview('');

    // Optional: Save review to backend
    try {
      const res = await fetch(`http://localhost:5000/api/movies/${movie.id}/review`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newReview)
      });
      await res.json();
    } catch(err) {
      console.error(err);
    }
  };

  return (
    <div className="movie-card" onClick={onClick}> {/* added onClick here */}
      <img src={movie.image} alt={movie.title} />
      <h2>{movie.title}</h2>
      <p><strong>{movie.year}</strong> | Director: {movie.director}</p>
      <p>Hero: {movie.hero} | Heroine: {movie.heroine}</p>
      <p>{movie.shortStory}</p>

      <div className="stars">
        {[1,2,3,4,5].map(star => (
          <span
            key={star}
            className={star <= rating ? "filled" : "empty"}
            onClick={(e) => { e.stopPropagation(); handleClick(star); }} // prevent card click
          >★</span>
        ))}
      </div>

      <textarea
        placeholder="Write your review..."
        value={review}
        onChange={(e) => setReview(e.target.value)}
      />

      <button onClick={(e) => { e.stopPropagation(); handleSubmit(); }}>Submit Review</button>

      <div className="reviews">
        {reviewsList.map((r, idx) => (
          <div key={idx} className="review-item">
            <span className="review-stars">{'★'.repeat(r.rating)+'☆'.repeat(5-r.rating)}</span>
            <p>{r.text}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MovieCard;
