import React, { useState } from "react";
import MovieCard from "./components/MovieCard";
import MovieDetailModal from "./components/MovieDetailModal";
import "./App.css";

function App() {
  const [selectedMovie, setSelectedMovie] = useState(null);

  const movies = [
    {
      id: 1,
      title: "Squid Game",
      year: 2025,
      seasons: "3 Seasons",
      genre: "Thriller",
      shortStory:
        "Hundreds of cash-strapped players accept a strange invitation to compete in children's games. Inside, a tempting prize awaits â€” with deadly high stakes.",
      image: "/images/bg1.jpg",
      starring: "Lee Jung-jae, Lee Byung-hun, Yim Si-wan",
      director: "Hwang Dong-hyuk",
      episodes: 9,
      duration: "50 min each",
      rating: 4.8,
    },
    {
      id: 2,
      title: "True Beauty",
      year: 2021,
      seasons: "1 Season",
      genre: "Romance, Comedy",
      shortStory:
        "A shy high school girl transforms herself using makeup and learns the true meaning of beauty and confidence.",
      image: "/images/bg2.jpg",
      starring: "Moon Ga-young, Cha Eun-woo, Hwang In-yeop",
      director: "Kim Sang-hyub",
      episodes: 16,
      duration: "60 min each",
      rating: 4.6,
    },
  ];

  return (
    <div className="App">
      <h1 className="app-title">ðŸŽ¬ RifLux</h1>

      <div className="movie-grid">
        {movies.map((movie) => (
          <MovieCard
            key={movie.id}
            movie={movie}
            onClick={() => setSelectedMovie(movie)}
          />
        ))}
      </div>

      {selectedMovie && (
        <MovieDetailModal
          movie={selectedMovie}
          onClose={() => setSelectedMovie(null)}
        />
      )}
    </div>
  );
}

export default App;
