import React, { useState } from 'react';
import './MovieCard.css';

function MovieCard({ movie }) {
  const [rating, setRating] = useState(movie.rating);
  const [review, setReview] = useState('');

  // ✅ Preloaded comments per movie
  const [reviewsList, setReviewsList] = useState([
    ...(movie.id === 1 ? [
      { name: "Albert", rating: 5, text: "Absolutely thrilling! Can't stop watching." },
      { name: "Sofia", rating: 4, text: "Great story, but some parts are intense!" }
    ] : []),
    ...(movie.id === 2 ? [
      { name: "David", rating: 5, text: "Heartwarming and funny. Loved the characters!" },
      { name: "Nora", rating: 4, text: "Beautiful story about confidence and love." }
    ] : []),
    ...(movie.id === 3 ? [
      { name: "Ken", rating: 5, text: "Mind-blowing plot! The visuals were incredible." },
      { name: "Riya", rating: 4, text: "So intense and unpredictable. A must-watch!" }
    ] : []),
    ...(movie.id === 4 ? [
      { name: "Priya", rating: 3, text: "Cute romantic film with lovely songs." },
      { name: "Arjun", rating: 4, text: "Paris backdrop was stunning!" }
    ] : []),
    ...(movie.id === 5 ? [
      { name: "Vikram", rating: 5, text: "Mass movie! Thalaivar swag unmatched!" },
      { name: "Latha", rating: 4, text: "Classic dialogues and family emotions." }
    ] : []),
    ...(movie.id === 6 ? [
      { name: "Meera", rating: 5, text: "Genius! Every scene has meaning." },
      { name: "Anish", rating: 4, text: "Deserves every Oscar it won." }
    ] : []),
    ...(movie.id === 7 ? [
      { name: "Leo", rating: 5, text: "Emotional and terrifying! Zombies done right." },
      { name: "Harini", rating: 4, text: "Cried at the end. Powerful message." }
    ] : []),
    ...(movie.id === 8 ? [] : []), // ✅ No reviews yet
    ...(movie.id === 9 ? [
      { name: "Maya", rating: 4, text: "Stylish, clever, and full of attitude." },
      { name: "Daniel", rating: 5, text: "Loved the mafia-lawyer twist!" }
    ] : []),
    ...(movie.id === 10 ? [] : []), // ✅ No reviews yet
    ...(movie.id === 11 ? [
      { name: "Aria", rating: 5, text: "Visually stunning and heartbreaking." },
      { name: "Mohan", rating: 4, text: "The ending still hurts in a beautiful way." }
    ] : []),
    ...(movie.id === 12 ? [
      { name: "Karan", rating: 5, text: "Unstoppable energy and emotion!" },
      { name: "Tara", rating: 4, text: "Epic friendship and music." }
    ] : []),
    ...(movie.id === 13 ? [
      { name: "Sanjay", rating: 4, text: "Zombies + history = brilliant combo." },
      { name: "Hana", rating: 5, text: "Loved the political twists." }
    ] : []),
    ...(movie.id === 14 ? [
      { name: "Oliver", rating: 5, text: "Smart and stylish. Classic detective vibes." },
      { name: "Divya", rating: 4, text: "Cumberbatch is perfect in this role." }
    ] : []),
    ...(movie.id === 15 ? [
      { name: "Ethan", rating: 5, text: "Intense! The music scenes gave me chills." },
      { name: "Sneha", rating: 4, text: "So much passion and obsession in one movie." }
    ] : []),
    ...(movie.id === 16 ? [
      { name: "Vivek", rating: 5, text: "Keeps your brain spinning even after it ends." },
      { name: "Grace", rating: 4, text: "Dream within a dream! Legendary." }
    ] : []),
    ...(movie.id === 17 ? [
      { name: "Minji", rating: 5, text: "Powerful revenge drama — perfect acting!" },
      { name: "Aaron", rating: 4, text: "Slow burn but totally worth it." }
    ] : []),
    ...(movie.id === 18 ? [] : []), // ✅ No reviews yet
    ...(movie.id === 19 ? [
      { name: "Elsa", rating: 5, text: "Into the Unknown! Beautiful visuals." },
      { name: "Ravi", rating: 4, text: "Kids loved it — and so did I." }
    ] : []),
    ...(movie.id === 20 ? [
      { name: "Sam", rating: 4, text: "Epic finale! Loved the storytelling." },
      { name: "Leah", rating: 5, text: "Incredible performances by the cast." }
    ] : [])
  ]);

  const handleClick = (star) => setRating(star);

  const handleSubmit = () => {
    if (review.trim() === '') return;
    const newReview = { rating, text: review, name: "You" };
    setReviewsList([...reviewsList, newReview]);
    setReview('');
  };

  return (
    <div className="movie-card">
      <img src={movie.image} alt={movie.title} />
      <h2>{movie.title}</h2>
      <p><strong>{movie.year}</strong> | Director: {movie.director}</p>
      <p>Hero: {movie.hero} | Heroine: {movie.heroine}</p>
      <p>{movie.shortStory}</p>

      <div className="stars">
        {[1,2,3,4,5].map(star => (
          <span
            key={star}
            className={star <= rating ? "filled" : "empty"}
            onClick={() => handleClick(star)}
          >★</span>
        ))}
      </div>

      <textarea
        placeholder="Write your review..."
        value={review}
        onChange={(e) => setReview(e.target.value)}
      />

      <button onClick={handleSubmit}>Submit Review</button>

      {/* ✅ Show all reviews */}
      <div className="reviews">
        {reviewsList.length > 0 ? (
          reviewsList.map((r, idx) => (
            <div key={idx} className="review-item">
              <span className="review-stars">{'★'.repeat(r.rating)+'☆'.repeat(5-r.rating)}</span>
              <p><strong>{r.name}:</strong> {r.text}</p>
            </div>
          ))
        ) : (
          <p className="no-reviews">No reviews yet. Be the first to comment!</p>
        )}
      </div>
    </div>
  );
}

export default MovieCard;
