import React, { useState } from "react";
import { movies } from "./movies"; // your local movie data
import "./MovieDetails.css";

export default function MoviePlatform({ movies: apiMovies }) {
  const [selectedMovie, setSelectedMovie] = useState(null);

  // Use API movies if passed, otherwise use local movies
  const moviesToUse = apiMovies && apiMovies.length > 0 ? apiMovies : movies;

  return (
    <div className="movie-container">
      {!selectedMovie ? (
        <div className="movie-grid">
          {moviesToUse.map((movie) => (
            <div
              key={movie.id}
              className="movie-card"
              onClick={() => setSelectedMovie(movie)}
            >
              <img src={`/images/${movie.cardImage}`} alt={movie.title} />
              <h3>{movie.title}</h3>
            </div>
          ))}
        </div>
      ) : (
        <div className="movie-detail">
          <div
            className="movie-bg"
            style={{
              backgroundImage: `linear-gradient(to bottom, rgba(0,0,0,0.2), rgba(0,0,0,0.8)), url(/images/${selectedMovie.bgImage})`,
            }}
          ></div>

          <div className="movie-info">
            <h1>{selectedMovie.title}</h1>
            <p><strong>Casting:</strong> {selectedMovie.casting}</p>
            <p><strong>Year:</strong> {selectedMovie.year}</p>
            <p><strong>Duration:</strong> {selectedMovie.duration}</p>
            <p>{selectedMovie.description}</p>

            <div className="review-section">
              <h3>Rate this movie</h3>
              <textarea placeholder="Write your review..."></textarea>
              <button>Submit</button>
            </div>

            <button
              className="back-btn"
              onClick={() => setSelectedMovie(null)}
            >
              ‚Üê Back to list
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
