import React, { useState } from "react";
import "./MovieCard.css";

function MovieCard({ movie }) {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [rating, setRating] = useState(0);
  const [review, setReview] = useState("");
  const [reviews, setReviews] = useState([
    { name: "Albert", text: "Absolutely thrilling! Can't stop watching.", stars: 5 },
    { name: "Sofia", text: "Great story, but some parts are intense!", stars: 4 }
  ]);

  const handleReviewSubmit = () => {
    if (review.trim() === "") return;
    const newReview = { name: "You", text: review, stars: rating };
    setReviews([...reviews, newReview]);
    setReview("");
    setRating(0);
  };

  return (
    <div className="movie-card">
      {/* Movie Card */}
      <div
        className="card"
        onClick={() => setSelectedMovie(movie)}
      >
        <img src={movie.image} alt={movie.title} />
        <h3>{movie.title}</h3>
      </div>

      {/* Movie Modal */}
      {selectedMovie && (
        <div
          className="modal"
          onClick={() => setSelectedMovie(null)}
        >
          {/* üîß Full-screen background image */}
          <div
            className="modal-content"
            onClick={(e) => e.stopPropagation()}
            style={{
              backgroundImage: `url(${selectedMovie.background})`, // üîß changed here
              backgroundSize: "cover", // üîß changed here
              backgroundPosition: "center", // üîß changed here
              backgroundRepeat: "no-repeat", // üîß added
              width: "100vw",
              height: "100vh",
              color: "white",
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
              padding: "40px",
            }}
          >
            <h2>{selectedMovie.title}</h2>
            <p>
              <strong>Hero:</strong> {selectedMovie.hero} <br />
              <strong>Heroine:</strong> {selectedMovie.heroine} <br />
              <strong>Director:</strong> {selectedMovie.director} <br />
              <strong>Year:</strong> {selectedMovie.year}
            </p>

            <p className="description">{selectedMovie.description}</p>

            {/* ‚≠ê Star Rating */}
            <div className="stars">
              {[1, 2, 3, 4, 5].map((num) => (
                <span
                  key={num}
                  onClick={() => setRating(num)}
                  style={{
                    color: num <= rating ? "gold" : "gray",
                    fontSize: "25px",
                    cursor: "pointer",
                  }}
                >
                  ‚òÖ
                </span>
              ))}
            </div>

            {/* Review box */}
            <textarea
              value={review}
              onChange={(e) => setReview(e.target.value)}
              placeholder="Write your review here..."
            ></textarea>
            <button onClick={handleReviewSubmit}>Submit Review</button>

            {/* Reviews List */}
            <div className="reviews">
              <h4>What others said:</h4>
              {reviews.map((r, i) => (
                <div key={i} className="review">
                  <strong>{r.name}:</strong> {r.text}{" "}
                  <span style={{ color: "gold" }}>
                    {"‚òÖ".repeat(r.stars)}
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default MovieCard;
