import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './MovieCard.css'; // reuse your same styling

function MovieDetail() {
  const { title } = useParams();
  const navigate = useNavigate();

  // You can later replace this with backend data
  const movieData = {
    'Squid Game': {
      year: '2021',
      director: 'Hwang Dong-hyuk',
      hero: 'Lee Jung-jae',
      heroine: 'HoYeon Jung',
      desc: 'Hundreds of cash-strapped players accept a strange invitation to compete in deadly games for money.',
      rating: 5,
      img: '/images/squid_game.jpg',
    },
    'Inception': {
      year: '2010',
      director: 'Christopher Nolan',
      hero: 'Leonardo DiCaprio',
      heroine: 'Elliot Page',
      desc: 'A thief enters dreams to steal secrets.',
      rating: 4,
      img: '/images/inception.jpg',
    },
    'Oppenheimer': {
      year: '2023',
      director: 'Christopher Nolan',
      hero: 'Cillian Murphy',
      heroine: 'Emily Blunt',
      desc: 'The story of J. Robert Oppenheimer and the atomic bomb.',
      rating: 5,
      img: '/images/oppenheimer.jpg',
    },
    'Parasite': {
      year: '2019',
      director: 'Bong Joon-ho',
      hero: 'Song Kang-ho',
      heroine: 'Cho Yeo-jeong',
      desc: 'A poor family infiltrates a wealthy household.',
      rating: 4,
      img: '/images/parasite.jpg',
    },
  };

  const movie = movieData[title];

  if (!movie) {
    return <div className="movie-detail"><h2>Movie not found</h2></div>;
  }

  return (
    <div
      className="movie-detail-page"
      style={{
        backgroundImage: `url(${movie.img})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        color: 'white',
        padding: '100px 50px',
        backdropFilter: 'blur(5px)',
      }}
    >
      <div className="movie-detail-box">
        <img src={movie.img} alt={movie.title} className="detail-image" />
        <div className="detail-info">
          <h1>{title}</h1>
          <p><strong>{movie.year}</strong> | Director: {movie.director}</p>
          <p>Hero: {movie.hero} | Heroine: {movie.heroine}</p>
          <p>{movie.desc}</p>
          <p>{'⭐'.repeat(movie.rating)}</p>

          <button onClick={() => navigate('/')} className="back-btn">
            ← Back to Reviews
          </button>
        </div>
      </div>
    </div>
  );
}

export default MovieDetail;
