import React, { useState } from 'react';
import './MovieCard.css';

function MovieCard({ movie }) {
  const [rating, setRating] = useState(movie.rating);
  const [review, setReview] = useState('');
  const [reviewsList, setReviewsList] = useState(movie.reviews || []);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleClick = (star) => setRating(star);

  const handleSubmit = async () => {
    if (review.trim() === '') return;

    const newReview = { rating, text: review };
    setReviewsList([...reviewsList, newReview]);
    setReview('');

    try {
      await fetch(`http://localhost:5000/api/movies/${movie.id}/review`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newReview),
      });
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <>
      <div className="movie-card">
        {/* üñºÔ∏è Clicking image opens modal */}
        <img
          src={movie.image}
          alt={movie.title}
          onClick={() => setIsModalOpen(true)}
          className="movie-thumb"
        />

        <h2>{movie.title}</h2>
        <p><strong>{movie.year}</strong> | Director: {movie.director}</p>
        <p>Hero: {movie.hero} | Heroine: {movie.heroine}</p>
        <p>{movie.shortStory}</p>

        {/* ‚≠ê Star rating */}
        <div className="stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={star <= rating ? 'filled' : 'empty'}
              onClick={() => handleClick(star)}
            >
              ‚òÖ
            </span>
          ))}
        </div>

        {/* ‚úçÔ∏è Write review */}
        <textarea
          placeholder="Write your review..."
          value={review}
          onChange={(e) => setReview(e.target.value)}
        />

        <button onClick={handleSubmit}>Submit Review</button>

        {/* üí¨ Review list */}
        <div className="reviews">
          <h4>User Reviews</h4>
          {reviewsList.length > 0 ? (
            reviewsList.map((r, idx) => (
              <div key={idx} className="review-item">
                <span className="review-stars">
                  {'‚òÖ'.repeat(r.rating) + '‚òÜ'.repeat(5 - r.rating)}
                </span>
                <p>{r.text}</p>
              </div>
            ))
          ) : (
            <p className="no-reviews">No reviews yet. Be the first to comment!</p>
          )}
        </div>
      </div>

      {/* üñºÔ∏è Full-screen background modal */}
      {isModalOpen && (
        <div className="modal" onClick={() => setIsModalOpen(false)}>
          <img
            src={process.env.PUBLIC_URL + movie.background}  // ‚úÖ changed
            alt={`${movie.title} Background`}
            className="modal-image"
          />
        </div>
      )}
    </>
  );
}

export default MovieCard;
