import React, { useState } from "react";
import "./MovieCard.css";

function MovieCard({ movie }) {
  const [showModal, setShowModal] = useState(false);
  const [rating, setRating] = useState(0);
  const [hover, setHover] = useState(0);
  const [review, setReview] = useState("");

  // open modal when movie clicked
  const handleCardClick = () => {
    setShowModal(true);
  };

  // close modal
  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <>
      {/* Movie Card */}
      <div className="movie-card" onClick={handleCardClick}>
        <img src={movie.image} alt={movie.title} className="movie-image" />
        <div className="movie-info">
          <h3>{movie.title}</h3>
          <p>{movie.year}</p>
        </div>
      </div>

      {/* Modal for full screen */}
      {showModal && (
        <div className="modal-overlay" onClick={handleCloseModal}>
          {/* ðŸ”¥ CHANGED: added background image styling */}
          <div
            className="modal-content"
            onClick={(e) => e.stopPropagation()}
            style={{
              backgroundImage: `url(${movie.background})`, // ðŸ”¥ use movie.background
              backgroundSize: "cover",
              backgroundPosition: "center",
              width: "100vw",
              height: "100vh",
              overflowY: "auto",
              color: "white",
              padding: "40px",
            }}
          >
            <button
              onClick={handleCloseModal}
              style={{
                position: "absolute",
                top: "20px",
                right: "30px",
                fontSize: "25px",
                background: "none",
                color: "white",
                border: "none",
                cursor: "pointer",
              }}
            >
              âœ–
            </button>

            <div style={{ background: "rgba(0,0,0,0.6)", padding: "20px", borderRadius: "10px" }}>
              <h1>{movie.title}</h1>
              <p><b>Hero:</b> {movie.hero}</p>
              <p><b>Heroine:</b> {movie.heroine}</p>
              <p><b>Director:</b> {movie.director}</p>
              <p><b>Year:</b> {movie.year}</p>
              <p style={{ maxWidth: "600px" }}>{movie.description}</p>
            </div>

            {/* Rating Stars */}
            <div style={{ marginTop: "20px" }}>
              {[...Array(5)].map((star, index) => {
                index += 1;
                return (
                  <span
                    key={index}
                    style={{
                      cursor: "pointer",
                      color: index <= (hover || rating) ? "#ffcc00" : "white",
                      fontSize: "30px",
                    }}
                    onClick={() => setRating(index)}
                    onMouseEnter={() => setHover(index)}
                    onMouseLeave={() => setHover(rating)}
                  >
                    â˜…
                  </span>
                );
              })}
            </div>

            {/* Review Box */}
            <textarea
              placeholder="Write your review..."
              value={review}
              onChange={(e) => setReview(e.target.value)}
              style={{
                width: "100%",
                marginTop: "15px",
                backgroundColor: "#222",
                color: "white",
                border: "1px solid #555",
                borderRadius: "8px",
                padding: "10px",
                height: "80px",
              }}
            />

            <button
              style={{
                backgroundColor: "#e50914",
                color: "white",
                border: "none",
                padding: "10px 20px",
                borderRadius: "8px",
                marginTop: "10px",
                cursor: "pointer",
              }}
            >
              Submit Review
            </button>

            {/* Example existing comments */}
            <div style={{ marginTop: "20px" }}>
              <h3>What others said:</h3>
              <p><b>Albert:</b> "Absolutely thrilling! Can't stop watching."</p>
              <p><b>Sofia:</b> "Great story, but some parts are intense!"</p>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default MovieCard;
